  const foundIndex = pizzas.findIndex(pizza => pizza.id == req.params.id);

  if (foundIndex < 0) return res.sendStatus(404);

  const updatedPizza = {...pizzas[foundIndex], ...req.body};

  pizzas[foundIndex] = updatedPizza;

