      .then((response) => {
        if (!response.ok)
          throw new Error(
            `fetch error : ${response.status} : ${response.statusText}`
          );
        return response.json();
      })
      .then((pizzas) => setPizzas(pizzas))
      .catch((err) => {
        console.error("HomePage::error: ", err);
