    const timer = setTimeout(() => {
      setMessage('3 secondes se sont √©coul√©es!');
    }, 3000);

    // Nettoyage du timer pour √©viter des fuites de m√©moire si le composant est d√©mont√© avant que le timer se d√©clenche
    return () => clearTimeout(timer);
